#!/usr/bin/ksh

echo "\n"; lparstat -i | egrep "Node Name|Partition Name"; echo "\n========== CPU / virtual CPU / Memory ==========="; lparstat -i | egrep "Type|Mode"; lparstat -i | egrep "Entitled Capacity|Minimum Capacity|Maximum Capacity|Physical CPUs"; echo "===================="; lparstat -i | egrep "Online Virtual CPUs|Maximum Virtual CPUs|Minimum Virtual CPUs"; echo "===================="; lparstat -i | egrep "Online Memory|Maximum Memory|Minimum Memory"; echo "\n========== Time Synchronization ================="; date; sudo lssrc -ls xntpd | egrep "Leap indicator|Sys peer|Reference time|Status|active"; echo "\n========== HACMP Cluster Status ================="; sudo lssrc -ls clstrmgrES | egrep -i "state|timer|DNP|NodeName|Pg|Pv|Pc"; sudo /usr/es/sbin/cluster/clstat -o; echo "\n========== GPFS Status =========================="; sudo /usr/lpp/mmfs/bin/mmgetstate -a; sudo /usr/lpp/mmfs/bin/mmlscluster; echo "\n========== Error Report (P&&H) =================="; errpt -d H; echo "\n";
